INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'09ad324e-e22e-46eb-9291-e8b40069d919',0,N'QuickReview',N'The world''s most popular portable gaming device is even more fun. Now available in black and white, iPod touch includes iOS 5 with over 200 new features, like iMessage, Notification Center, and Twitter integration. Send free, unlimited text messages over Wi-Fi with iMessage. Record HD video and make FaceTime calls. Visit the App Store to choose from over 500,000 apps. iPod touch also features iCloud, which stores your music, photos, apps, and moreÑand wirelessly pushes them to all your devices.',2,NULL,N'v-b005gs3cfg',N'20120319 22:00:48.840',N'20120319 22:00:48.840',N'EditorialReview',N'en-us');
