INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'1527c24a-1913-441e-b4e6-eb6b98307673',0,N'QuickReview',N'The Sony E-Series Walkman MP3 Player delivers up to 50 hours of music -with video and photo playback plus FM radio just to sweeten the deal. With delicious colors, Windows drag and drop functionality and digitally restorative Sony Clear Audio Technologies, the E-Series is bling to the ears and eyes.',2,NULL,N'v-b005eqs9ks',N'20120319 21:56:43.243',N'20120319 21:56:43.243',N'EditorialReview',N'en-us');
