INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'1a0f7631-9f50-451d-bf16-d745aebe91ab',0,N'QuickReview',N'From students to social networking enthusiasts, home office to the kitchen table, the Samsung Series 3 laptop was designed to keep you connected, entertained, and looking stylish. Offering a seamless blend of style and capability, this model (NP300V4A-A04US) features a 14-inch high-def display, generous 500 GB hard drive, and 6 GB of installed RAM for excellent multitasking. It''s powered by the 2.45 GHz Intel Core i5 2450M processor.  It also offers integrated Intel HD 3000 graphics for fast, vivid visuals.',2,NULL,N'v-b006kyy0ga',N'20120319 21:59:46.963',N'20120319 21:59:46.963',N'EditorialReview',N'en-us');
