INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'9000bc87-88fb-45f1-b75a-8036f1aafe75',0,N'QuickReview',N'Experience brilliant Full HD 1080p picture quality with Sony''s 46-inch KDL-46NX720 LCD HDTV with 3D. This HDTV features Dynamic Edge LED backlighting technology with local dimming, for serious contrast levels, deep blacks, and brilliant whites. It also features MotionFlow XR 240 for crisp, fluid motion in fast-action scenes. Pair with Sony 3D glasses and you''ll be ready for another dimension of entertainment, with immersive playback of the latest 3D titles. Or use the built-in Wi-Fi to access connected entertainment options with BRAVIA Internet Video and Widgets, or your digital video, music, and photo files over your home network.',2,NULL,N'v-b004qxjj7w',N'20120319 21:56:25.463',N'20120319 21:56:25.463',N'EditorialReview',N'en-us');
