INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'a60b2c15-1a8d-43b6-81f4-7a751cbebb3c',0,N'QuickReview',N'This pocket-sized device turns your SAMSUNG Galaxy Tab™ 10.1 into a USB host, allowing you to connect compatible USB accessory devices such as keyboards, mice, thumb drives and more to your Tab™. The adapter enhances the capabilities of your Tab, providing a solution for user-friendly removable data storage or the sharing of your favorite multimedia files with your Tab. Insert a USB mouse or keyboard to improve enterprise efficiency at the office or at home. Simply connect a USB memory stick or USB cable from compatible devices to the USB port on the adapter and then insert the adapter into your Tab.',2,NULL,N'v-b005518j6q',N'20120319 22:00:20.837',N'20120319 22:00:20.837',N'EditorialReview',N'en-us');
INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'a6a928d8-77ff-43d7-be28-9081ce4b7d9a',0,N'QuickReview',N'The Samsung PL120 DualView 14.2-Megapixel Digital Camera features LCD viewfinders on both the front and rear of the camera, making it easy for you to take great self-portraits as well as set up your other shots clearly. Thanks to HD Movie, you can also use the PL120 to shoot high-definition video. Digital image stabilization creates clear, blur-free photos, while Smart Auto 2.0 optimizes settings for pictures and video in any lighting.',2,NULL,N'v-b004l3jpx0',N'20120319 21:58:44.867',N'20120319 21:58:44.867',N'EditorialReview',N'en-us');
