INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'd37a100f-e799-4139-a7c1-a03d1ccf8de6',0,N'QuickReview',N'Enter a world of dazzling picture quality and unlimitedentertainment. This elegantly slim LED TV boasts Full HD 1080p forincredible detail and Edge LED backlighting for boosted contrast.Internet connectivity is at your fingertips, so you can watchYouTube™ clips and access online HD movies, music and more.',2,NULL,N'v-b006u1vgho',N'20120319 21:55:56.290',N'20120319 21:55:56.290',N'EditorialReview',N'en-us');
