INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'f0a9de68-9a15-453d-8081-9b043fdaf4c8',0,N'QuickReview',N'This sixth-generation iPod nano is now upgradeable to Apple''s new nano 1.2 software. Simply connect to iTunes and upgrade, and you''ll be able to access the newer features. These include the ability to navigate with large icons displayed one at a time, a choice of 18 different clock face designs, and the ability to track your steps, distance, pace, and time while exercising, without the need for a separate shoe sensor or receiver.',2,NULL,N'v-b002l6hea0',N'20120319 22:00:46.823',N'20120319 22:00:46.823',N'EditorialReview',N'en-us');
INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'f0dcebf2-b5cf-4bd6-ae21-933e26e48882',0,N'QuickReview',N'Enjoy high-definition entertainment from Sony in any room with the BX320 series. Featuring 720p HD resolution, five HD inputs, a USB port for your digital media, and Sony technologies for a great picture no matter the environment, the 32-inch KDL-32BX320 is sure to fit into your home as well as your budget.',2,NULL,N'v-b004hyg9sm',N'20120319 21:56:18.040',N'20120319 21:56:18.040',N'EditorialReview',N'en-us');
INSERT INTO [EditorialReview] ([EditorialReviewId],[Priority],[Source],[Content],[ReviewState],[Comments],[ItemId],[LastModified],[Created],[Discriminator],[Locale]) VALUES (N'f134d196-779b-4bde-b7ba-aacd7c4ea382',0,N'QuickReview',N'Play back your favorite songs whenever and wherever you feel the need with this portable and lightweight MP3 player from Samsung. The Samsung YP-F1Z may be small in size, but it is huge in features. Offering support for MP3, WAV, WMA, ASF, Ogg and WMA DRM files it outdoes the competition. The Samsung YP-F1 also boasts a built in FM tuner, voice recorder and big sound with its SRS WOW 3d sound. With the 1 GB flash memory, and built in Li-Polymer battery you can listen to up to 10 hours of continuous playback!',2,NULL,N'v-b000eopbzg',N'20120319 21:59:12.073',N'20120319 21:59:12.073',N'EditorialReview',N'en-us');
